import React, { Component } from 'react';
import "./waniwani.css";
import iwakabe from './image/iwakabe.jpg';


export default class Cooking extends Component  {
    constructor (props) {
        super(props);
        this.state = {
            location:0,
            Image:"",
            Name:"",
            Ans:"",
            level:"easy"
        };
    }

    MakeGate = () =>{
        return(
        <div className="Gate">
        <table className="test">
            <tbody>
                <tr>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                </tr>
                <tr>
                <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td></td>
                    <td></td>
                    <td></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                </tr>
                <tr>
                <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td></td>
                    <td></td>
                    <td></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                </tr>
                <tr>
                <td><img src={iwakabe} height="30px" width="30px"/></td>
                    <td></td>
                    <td></td>
                    <td></td>
                    <td><img src={iwakabe} height="30px" width="30px"/></td>
                </tr>
            </tbody>
        </table>
    </div>
        );
    }


    HitWani = () =>{
        console.log("hit wani")
    }

    ClickStart = () =>{
        this.intervalId = setInterval(()=>{
            this.rand_WaniUp();
        }, 2000);
    }

    rand_WaniUp = () =>{
        const {location} = this.state;

        var a = Math.floor( Math.random() * 5)+1 ;
        console.log(a);

        this.setState({
            location:a
        })
    }

    render() {
        const {location} = this.state;
        console.log("location:"+location);

        return (<div>
            <div className="Header">
                <h1>ワニワニパニック</h1>
                <h3>誰が出てくるかな？ 当ててみて</h3>
                <a>レベル: </a>
                <select name="level" onChange={(e) => this.setState({level:e.target.value})}>
                    <option value="easy"  >簡単</option>
                    <option value="nomal" >普通</option>
                    <option value="difficult">難しい</option>
                </select>
                <input type="button" id="StButton"　className="StButton" value="スタート" onClick={this.ClickStart}></input><br/>
            </div>
            <div>
                {this.MakeGate()}
                {this.MakeGate()}
                {this.MakeGate()}
                {this.MakeGate()}
                {this.MakeGate()}
            </div>
            {
                (() => {
                    if(location===1){
                        return(<div className="WaniWani1"  onClick={this.HitWani}></div>);
                    }else if(location===2){
                        return(<div className="WaniWani2"  onClick={this.HitWani}></div>);
                    }else if(location===3){
                        return(<div className="WaniWani3" onClick={this.HitWani}></div>);
                    }else if(location===4){
                        return(<div className="WaniWani4"  onClick={this.HitWani}></div>);
                    }else if(location===5){
                        return(<div className="WaniWani5"  onClick={this.HitWani}></div>);
                    }
                    
                })
            }
            
            
            <div className="borderline"></div>
            
        </div>);
    }
  }
